select * from 
(
	select 

	from bas_cardreader
	where del_flag=#{DEL_FLAG_NORMAL}

	union all

	select 

	from bas_codeprinter
	where del_flag=#{DEL_FLAG_NORMAL}

	union all

	select 

	from bas_electronsteelyard
	where del_flag=#{DEL_FLAG_NORMAL}

	union all

	select 

	from bas_iphoto
	where del_flag=#{DEL_FLAG_NORMAL}

	union all

	select 

	from bas_pad
	where del_flag=#{DEL_FLAG_NORMAL}
) equipment
where id=#{id}

