mapper.select:
select 

from kpi_perform_station a

where a.id=#{id}

mapper.update:
update kpi_perform_station set
	perform_id=#{performType.id},
	station_id=#{workStationInfos.id},
	update_by=#{updateBy.id},
	update_date=#{updateDate}
where id=#{id}

update kpi_perform_station set
	del_flag=#{DEL_FLAG_DELETE}
where id=#{id}
