mapper.select:
select 

from tec_flow_process a 

where a.id=#{id}

select 

from tec_flow_process a 

where a.del_flag=#{DEL_FLAG_NORMAL}

	and b.flowName like concat('%',#{flow.flowName},'%')


	and a.flow_id=#{flow.id}

order by a.sort

mapper.update:
update tec_flow_process set
	del_flag=#{DEL_FLAG_DELETE}
where id=#{id}
